/**
 * Checks if a given value is a plain object.
 *
 * @param {object} value - The value to check.
 * @returns {value is Record<PropertyKey, any>} - True if the value is a plain object, otherwise false.
 *
 * @example
 * ```typescript
 * // 鉁咅煈?True
 *
 * isPlainObject({ });                       // 鉁? * isPlainObject({ key: 'value' });          // 鉁? * isPlainObject({ key: new Date() });       // 鉁? * isPlainObject(new Object());              // 鉁? * isPlainObject(Object.create(null));       // 鉁? * isPlainObject({ nested: { key: true} });  // 鉁? * isPlainObject(new Proxy({}, {}));         // 鉁? * isPlainObject({ [Symbol('tag')]: 'A' });  // 鉁? *
 * // 鉁咅煈?(cross-realms, node context, workers, ...)
 * const runInNewContext = await import('node:vm').then(
 *     (mod) => mod.runInNewContext
 * );
 * isPlainObject(runInNewContext('({})'));   // 鉁? *
 * // 鉂岎煈?False
 *
 * class Test { };
 * isPlainObject(new Test())           // 鉂? * isPlainObject(10);                  // 鉂? * isPlainObject(null);                // 鉂? * isPlainObject('hello');             // 鉂? * isPlainObject([]);                  // 鉂? * isPlainObject(new Date());          // 鉂? * isPlainObject(new Uint8Array([1])); // 鉂? * isPlainObject(Buffer.from('ABC'));  // 鉂? * isPlainObject(Promise.resolve({})); // 鉂? * isPlainObject(Object.create({}));   // 鉂? * isPlainObject(new (class Cls {}));  // 鉂? * isPlainObject(globalThis);          // 鉂?
 * ```
 */
declare function isPlainObject(value: unknown): value is Record<PropertyKey, any>;

export { isPlainObject };
